def get_top_pools(top_num: int = 10) -> dict:
    """
    A basic function to query the Uniswap V3 GraphQL data for the top X pools, by liquidity.
    :param top_num: the top top_num of pools to return
    :return: results_list containing the list of pools dictionaries
    """
    request_query = f"{{pools (orderBy: totalValueLockedETH, orderDirection: desc, first: {top_num}, skip: 4) \
        {{ \
            id, \
            token0 {{ id, symbol, name, decimals}}, \
            token1 {{ id, symbol, name, decimals}}, \
            token0Price, \
            token1Price, \
            volumeToken0, \
            volumeToken1, \
            untrackedVolumeUSD, \
            liquidity \
        }} \
    }}"
    req = requests.post(V3_UNISWAP_GRAPHQL_URL, json={"query": request_query})
    results_list = json.loads(req.text)
    return results_list["data"]["pools"]


def get_web3_provider() -> object:
    return Web3(Web3.HTTPProvider(W3_HTTP_PROVIDER))


async def get_pool_slot0(_pool_id: str, _w3_provider: "Web3") -> list:
    """
    Function to query the blockchain and gather realtime slot0 data for the liquidity pool.
    :param pool_id: The contract address for the V3 liquidity pool.
    :return: The list containing all slot0 data for the pool
    """
    lp_contract = _w3_provider.eth.contract(
        address=Web3.toChecksumAddress(_pool_id), abi=LP_ABI
    )
    slot0_data = lp_contract.functions.slot0().call()
    """ List from blockchain of : sqrtPriceX96 uint160, tick int24, 
      observationIndex uint16, observationCardinality uint16, 
      observationCardinalityNext uint16, feeProtocol uint8, unlocked bool
    """
    return [_pool_id, slot0_data]


async def get_pool_tickBitmap(_pool_id: str, _w3_provider: "Web3") -> list:
    """
    Function to query the blockchain and gather the tickBitmap for the liquidity pool.
    :param pool_id: The contract address for the V3 liquidity pool.
    :return: The  tickBitmap
    """
    lp_contract = _w3_provider.eth.contract(
        address=Web3.toChecksumAddress(_pool_id), abi=LP_ABI
    )
    tick_bitmap = lp_contract.functions.tickBitmap(0).call()
    return [_pool_id, tick_bitmap]


async def get_slot0_future(
    _pools_list: str, _w3_provider: "Web3", _future: Future
) -> Future:
    tmp_slot0 = await get_pool_slot0(_pools_list, _w3_provider)
    _future.set_result(tmp_slot0)


async def get_tickBitmap_future(
    _pools_list: str, _w3_provider: "Web3", _future: Future
) -> Future:
    tmp_bitmap = await get_pool_tickBitmap(_pools_list, _w3_provider)
    _future.set_result(tmp_bitmap)


def create_slot0_future(_pools_list: str, _w3_provider: "Web3") -> Future:
    tmp_future = Future()
    asyncio.create_task(get_slot0_future(_pools_list, _w3_provider, tmp_future))
    return tmp_future


def create_tickBitmap_future(_pools_list: str, _w3_provider: "Web3") -> Future:
    tmp_future = Future()
    asyncio.create_task(get_tickBitmap_future(_pools_list, _w3_provider, tmp_future))
    return tmp_future


###
async def main() -> None:
    slot0_futures = []
    bitmap_futures = []
    slot0_results = []
    bitmap_results = []

    pools_list = get_top_pools(50)
    w3 = get_web3_provider()
    # Loop through all pools obtained from the GraphQL query
    start_time = perf_counter()
    for cur_pool in pools_list:
        contract_address = cur_pool["id"]
        slot0_futures.append(create_slot0_future(contract_address, w3))
        bitmap_futures.append(create_tickBitmap_future(contract_address, w3))
    for slot0_task in asyncio.as_completed(slot0_futures):
        slot0_results.append(await slot0_task)
    for bitmap_task in asyncio.as_completed(bitmap_futures):
        bitmap_results.append(await bitmap_task)

    print(slot0_results)
    print(bitmap_results)
    end_time = perf_counter()
    print(end_time - start_time)


if __name__ == "__main__":
    asyncio.run(main())
