def get_top_pools(top_num:int = 10) -> dict:
    """
    A basic function to query the Uniswap V3 GraphQL data for the top X pools, by liquidity.

    :param top_num: the top top_num of pools to return
    :return: results_list containing the list of pools dictionaries
    """
    request_query = ("{\n\tpools (orderBy: totalValueLockedETH, orderDirection: desc, first: " + str(top_num) +", skip: 4)" +
    """
    {
        id, 
        token0 { id, symbol, name, decimals}, 
        token1 { id, symbol, name, decimals},
        token0Price, 
        token1Price, 
        volumeToken0, 
        volumeToken1, 
        untrackedVolumeUSD, 
        liquidity
    }
}""")
    req = requests.post(V3_UNISWAP_GRAPHQL_URL, json={'query': request_query})
    results_list = json.loads(req.text)
    return results_list['data']['pools']

def get_web3_provider() -> "Web3":
    return Web3(Web3.HTTPProvider(W3_HTTP_PROVIDER))

def get_pool_slot0(pool_id: str, w3_provider: "Web3") -> list:
    """
    Function to query the blockchain and gather realtime slot0 data for the liquidity pool.

    :param pool_id: The contract address for the V3 liquidity pool.
    :return: The list containing all slot0 data for the pool
    """
    lp_contract = w3_provider.eth.contract(address=Web3.toChecksumAddress(pool_id), abi=LP_ABI)
    slot0_data = lp_contract.functions.slot0().call()
    """ Slot0 list in form of: sqrtPriceX96 uint160, tick int24, 
      observationIndex uint16, observationCardinality uint16, 
      observationCardinalityNext uint16, feeProtocol uint8, unlocked bool
    """
    return [pool_id, slot0_data]

def get_pool_tickBitmap(pool_id: str, w3_provider: "Web3") -> list:
    """
        Function to query the blockchain and gather the tickBitmap for the liquidity pool.

        :param pool_id: The contract address for the V3 liquidity pool.
        :return: The  tickBitmap
        """
    lp_contract = w3_provider.eth.contract(address=Web3.toChecksumAddress(pool_id), abi=LP_ABI)
    tick_bitmap = lp_contract.functions.tickBitmap(0).call()
    return [pool_id, tick_bitmap]

###
def main() -> None:
    slot0_dict = {}
    tick_bitmaps = {}
    slot0_results = []
    bitmap_results = []

    pools_list = get_top_pools(50)
    w3 = get_web3_provider()
    # Loop through all pools obtained from the GraphQL query
    start_time = perf_counter()
    with concurrent.futures.ThreadPoolExecutor() as pool_executor:
        for cur_pool in pools_list:
            contract_address = cur_pool['id']
            if not (contract_address in slot0_dict.keys()):
                slot0_results.append(pool_executor.submit(get_pool_slot0, *[contract_address, w3]))
            if not (contract_address in tick_bitmaps.keys()):
                bitmap_results.append(pool_executor.submit(get_pool_tickBitmap, *[contract_address,w3]))
        for slot0 in concurrent.futures.as_completed(slot0_results):
            slot0_result = slot0.result()
            if not (slot0_result[0] in slot0_dict.keys()):
                slot0_dict[slot0_result[0]] = slot0_result[1]
        for bitmap in concurrent.futures.as_completed(bitmap_results):
            bitmap_result = bitmap.result()
            if not (bitmap_result[0] in tick_bitmaps.keys()):
                tick_bitmaps[bitmap_result[0]] = bitmap_result[1]
    print(slot0_dict)
    print(tick_bitmaps)
    end_time = perf_counter()
    print(end_time - start_time)

if __name__ == "__main__":
    main()